update tbl_bible_tamil set verse='அப்பொழுது ஆதாரேசரைச் சேவிக்கிற சகல ராஜாக்களும் தாங்கள் இஸ்ரவேலுக்கு முன்பாக முறிய அடிக்கப்பட்டதைக் கண்டு, இஸ்ரவேலரோடே சமாதானம்பண்ணி, அவர்களைச் சேவித்தார்கள். அப்புறம் அம்மோன் புத்திரருக்கு உதவிசெய்ய சீரியர் பயப்பட்டார்கள்.' where book_id=9 and chapter_id=10 and verse_id= 19;
update tbl_bible_tamil set verse='அவன் எங்களிடத்தில் வந்து, பவுலினுடைய கச்சையை எடுத்துத் தன் கைகளையும் கால்களையும் கட்டிக்கொண்டு: இந்தக் கச்சையையுடையவனை எருசலேமிலுள்ள யூதர் இவ்விதமாய்க் கட்டிப் புறஜாதியார் கைகளில் ஒப்புக்கொடுப்பார்கள் என்று பரிசுத்த ஆவியானவர் சொல்லுகிறார் என்றான்.' where book_id=43 and chapter_id=21 and verse_id= 11;
update tbl_bible_tamil set verse='நூற்றுக்கு அதிபதியும், அவனோடேகூட இயேசுவைக் காவல் காத்திருந்தவர்களும், பூமியதிர்ச்சியையும் சம்பவித்த காரியங்களையும் கண்டு, மிகவும் பயந்து: மெய்யாகவே இவர் தேவனுடைய குமாரன் என்றார்கள்.' where book_id=39 and chapter_id=27 and verse_id= 54;
update tbl_bible_tamil set verse='பின்பு அநேக ஜனங்கள் அவரோடேகூடப் பிரயாணமாய்ப் போகையில், அவர்களிடமாய் அவர் திரும்பிப்பார்த்து:' where book_id=41 and chapter_id=14 and verse_id= 25;
update tbl_bible_tamil set verse='இவர் என்னுடைய நேசகுமாரன், இவரிடத்தில் பிரியமாயிருக்கிறேன் என்று சொல்லுகிற சத்தம் உன்னதமான மகிமையிலிருந்து அவருக்கு உண்டாகி, பிதாவாகிய தேவனால் அவர் கனத்தையும் மகிமையையும் பெற்றபோது,' where book_id=60 and chapter_id=1 and verse_id= 17;
update tbl_bible_tamil set verse='அவன் தாவீது ராஜாவின் சுகசெய்தியை விசாரிக்கவும், அவன் ஆதாரேசரோடு யுத்தம்பண்ணி, அவனை முறிய அடித்ததற்காக அவனுக்கு வினவுதல் சொல்லவும், தன் குமாரனாகிய அதோராமையும், பொன்னும் வெள்ளியும் வெண்கலமுமான சகலவிதத் தட்டுமுட்டுகளையும், அவனிடத்துக்கு அனுப்பினான்; ஆதாரேசர் தோயூவின்மேல் யுத்தம்பண்ணுகிறவனாயிருந்தான்.' where book_id=12 and chapter_id=18 and verse_id= 10;
update tbl_bible_tamil set verse='ஆவி என்னை உயர எடுத்துக்கொண்டது; நான் என் ஆவியின் உக்கிரத்தினாலே மனங்கசந்துபோனேன்; ஆனாலும் கர்த்தருடைய கரம் என்மேல் பலமாக இருந்தது.' where book_id=25 and chapter_id=3 and verse_id= 14;
update tbl_bible_tamil set verse='அவர் ஒலிவமலையின் அடிவாரத்துக்குச் சமீபமாய் வருகையில் திரளானகூட்டமாகிய சீஷரெல்லாரும் தாங்கள் கண்ட சகல அற்புதங்களையுங்குறித்துச் சந்தோஷப்பட்டு,' where book_id=41 and chapter_id=19 and verse_id= 37;
update tbl_bible_tamil set verse='பலிபீடத்துக்குச் சீத்திம் மரத்தால் தண்டுகளையும் பண்ணி, அவைகளை வெண்கலத்தகட்டால் மூடுவாயாக.' where book_id=1 and chapter_id=27 and verse_id= 6;
update tbl_bible_tamil set verse=' அன்றியும் நீங்கள் ஜெபம்பண்ணும்போது, அஞ்ஞானிகளைப்போல வீண் வார்த்தைகளை அலப்பாதேயுங்கள்; அவர்கள், அதிக வசனிப்பினால் தங்கள் ஜெபம் கேட்கப்படுமென்று நினைக்கிறார்கள்.' where book_id=39 and chapter_id=6 and verse_id= 7;
update tbl_bible_tamil set verse='பண்டிகைதோறும் அவர்களுக்கு ஒருவனை அவன் விடுதலையாக்குவது அவசியமாயிருந்தபடியால் அப்படிச் சொன்னான்.' where book_id=41 and chapter_id=23 and verse_id= 17;
