update tbl_bible_tamil set verse='நான் அவர்களோடு சமாதான உடன்படிக்கைசெய்து, துஷ்ட மிருகங்களை தேசத்தில் இராதபடிக்கு ஒழியப்பண்ணுவேன்; அவர்கள் சுகமாய் வனாந்தரத்தில் தாபரித்து, காடுகளில் நித்திரைபண்ணுவார்கள்.' where book_id=25 and chapter_id=34 and verse_id= 25;
