update tbl_bible_tamil set verse='அவர் பூமி உருண்டையின்மேல் வீற்றிருக்கிறவர்; அதின் குடிகள் வெட்டுக்கிளிகளைப்போல இருக்கிறார்கள்; அவர் வானங்களை மெல்லிய திரையாகப் பரப்பி, அவைகளைக் குடியிருக்கிறதற்கான கூடாரமாக விரிக்கிறார்.' where book_id=22 and chapter_id=40 and verse_id= 22;
update tbl_bible_tamil set verse='சாயங்காலத்தில், திராட்சத்தோட்டத்துக்கு எஜமான் தன் காரியக்காரனை நோக்கி: நீ வேலையாட்களை அழைத்து, பிந்திவந்தவர்கள் தொடங்கி முந்திவந்தவர்கள்வரைக்கும் அவர்களுக்குக் கூலி கொடு என்றான்.' where book_id=39 and chapter_id=20 and verse_id= 8;
update tbl_bible_tamil set verse='தங்கள் சீஷரையும் ஏரோதியரையும் அவரிடத்தில் அனுப்பினார்கள். அவர்கள் வந்து: போதகரே, நீர் சத்தியமுள்ளவரென்றும், தேவனுடைய மார்க்கத்தைச் சத்தியமாய்ப் போதிக்கிறவரென்றும், நீர் முகதாட்சிணியம் இல்லாதவராகையால் எவனைக்குறித்தும் உமக்குக் கவலையில்லையென்றும் அறிந்திருக்கிறோம்.' where book_id=39 and chapter_id=22 and verse_id= 16;
update tbl_bible_tamil set verse='மணவாளன் வரத் தாமதித்தபோது அவர்கள் எல்லாரும் நித்திரைமயக்கமடைந்து தூங்கிவிட்டார்கள்.' where book_id=39 and chapter_id=25 and verse_id= 5;
update tbl_bible_tamil set verse='இந்த ஜனத்தை நடத்துகிறவர்கள் எத்தருமாய், அவர்களால் நடத்தப்படுகிறவர்கள் நாசமடைகிறவர்களுமாய் இருக்கிறார்கள்.' where book_id=22 and chapter_id=9 and verse_id= 16;
update tbl_bible_tamil set verse='இப்போதும் எழுந்திருந்து வெளியே வந்து, உம்முடைய ஊழியக்காரரோடே அன்பாய்ப் பேசும்; நீர் வெளியே வராதிருந்தால், இன்று இரவு ஒருவரும் உம்மோடே தங்கியிருப்பதில்லை என்று கர்த்தர்மேல் ஆணையிடுகிறேன்; அப்பொழுது உம்முடைய சிறுவயது முதல் இதுவரைக்கும் உமக்கு நேரிட்ட எல்லாத் தீமையைப்பார்க்கிலும், அது உமக்கு அதிக தீமையாயிருக்கும் என்றான்.' where book_id=9 and chapter_id=19 and verse_id= 7;
update tbl_bible_tamil set verse='யூதா புத்திரரின் கோத்திரத்திலும், சிமியோன் புத்திரரின் கோத்திரத்திலும், அவர்கள் கொடுத்தவைகளும் பேர்பேராகச் சொல்லப்பட்டவைகளுமான பட்டணங்களின் நாமங்களாவன:' where book_id=5 and chapter_id=21 and verse_id= 10;
