update tbl_bible_tamil set verse='ஆகையால் நீர் அதை ஊராருக்கு முன்பாகவும், என் ஜனத்தின் மூப்பருக்கு முன்பாகவும் வாங்கிக்கொள்ளும்படி உமக்கு அறியப்பண்ணவேண்டும் என்றிருந்தேன்; நீர் அதைச் சுதந்தரமுறையாக மீட்டுக்கொள்ள மனதிருந்தால், மீட்டுக்கொள்ளும்; அதை மீட்டுக்கொள்ள மனதில்லாதிருந்தால், நான் அதை அறியும்படிக்கு எனக்குச் சொல்லும்; உம்மையும் உமக்குப்பின்பு என்னையும் தவிர, அதை மீட்கத்தக்கவன் வேறொருவனும் இல்லை என்றான்; அதற்கு அவன்: நான் அதை மீட்டுக்கொள்ளுகிறேன் என்றான்.
' where book_id=7 and chapter_id=4 and verse_id= 4;
update tbl_bible_tamil set verse='ஊருக்குத் திரும்பிப்போகும்போது, தன் இரதத்திலே உட்கார்ந்து, ஏசாயா தீர்க்கதரிசியின் ஆகமத்தை வாசித்துக்கொண்டிருந்தான்.' where book_id=43 and chapter_id=8 and verse_id=28;