update tbl_bible_tamil set verse='ஏனெனில், தேவன் தாமே கட்டி உண்டாக்கின அஸ்திபாரங்களுள்ள நகரத்துக்கு அவன் காத்திருந்தான்.' where book_id=57 and chapter_id=11 and verse_id= 10;
update tbl_bible_tamil set verse='மேலும் அவர்களைக் கொலைசெய்யும்படிக்கு அவைகளுக்கு உத்தரவு கொடுக்கப்படாமல், ஐந்துமாதமளவும் அவர்களை வேதனைப்படுத்தும்படிக்கு உத்தரவு கொடுக்கப்பட்டது; அவைகள் செய்யும் வேதனை தேளானது மனுஷரைக் கொட்டும்போது உண்டாகும் வேதனையைப்போலிருக்கும்.' where book_id=65 and chapter_id=9 and verse_id= 5;
update tbl_bible_tamil set verse='விசுவாசித்தவர்களாகிய நாமோ அந்த இளைப்பாறுதலில் பிரவேசிக்கிறோம்; அவருடைய கிரியைகள் உலகத்தோற்றமுதல் முடிந்திருந்தும்: இவர்கள் என்னுடைய இளைப்பாறுதலில் பிரவேசிப்பதில்லையென்று என்னுடைய கோபத்திலே ஆணையிட்டேன் என்றார்.' where book_id=57 and chapter_id=4 and verse_id= 3;
update tbl_bible_tamil set verse='அன்றியும், அவர்கள் ஆணையில்லாமல் ஆசாரியராக்கப்படுகிறார்கள்; இவரோ: நீர் மெல்கிசேதேக்கின் முறைமையின்படி என்றென்றைக்கும் ஆசாரியராயிருக்கிறீர் என்று கர்த்தர் ஆணையிட்டார், மனம்மாறாமலும் இருப்பார் என்று தம்முடனே சொன்னவராலே ஆணையோடே ஆசாரியரானார்.' where book_id=57 and chapter_id=7 and verse_id= 20;
update tbl_bible_tamil set verse='என் நாமம் தரிக்கப்பட்ட என் ஜனங்கள் தங்களைத் தாழ்த்தி, ஜெபம்பண்ணி, என் முகத்தைத் தேடி, தங்கள் பொல்லாத வழிகளைவிட்டுத் திரும்பினால், அப்பொழுது பரலோகத்திலிருக்கிற நான் கேட்டு, அவர்கள் பாவத்தை மன்னித்து, அவர்கள் தேசத்துக்கு க்ஷேமத்தைக் கொடுப்பேன்.' where book_id=13 and chapter_id=7 and verse_id= 14;
