update tbl_bible_tamil set verse='அறிவினாலே அறைகளில் அருமையும் இனிமையுமான சகலவிதப் பொருள்களும் நிறைந்திருக்கும்.' where book_id=19 and chapter_id=24 and verse_id= 4;
update tbl_bible_tamil set verse='பிதாக்களே, ஆதிமுதலிருக்கிறவரை நீங்கள் அறிந்திருக்கிறதினால் உங்களுக்கு எழுதுகிறேன். வாலிபரே, பொல்லாங்கனை நீங்கள் ஜெயித்ததினால் உங்களுக்கு எழுதுகிறேன். பிள்ளைகளே, நீங்கள் பிதாவை அறிந்திருக்கிறதினால் உங்களுக்கு எழுதுகிறேன்.' where book_id=61 and chapter_id=2 and verse_id = 13;