update tbl_bible set verse='கர்த்தாவே, உம்முடைய தீர்க்கதரிசிகளை அவர்கள் கொலைசெய்து, உம்முடைய பலிபீடங்களை இடித்துப்போட்டார்கள்; நான் ஒருவன்மாத்திரம் மீதியாயிருக்கிறேன், என் பிராணனையும் வாங்கத்தேடுகிறார்களே என்று இஸ்ரவேலருக்கு விரோதமாய் விண்ணப்பம்பண்ணினபோது,' where book_id=44 and chapter_id=11 and verse_id= 3;
update tbl_bible set verse='நான் அவர்களைப் புறஜாதிகளுக்குள்ளே சிதறடித்து, அவர்களை தேசங்களிலே தூற்றிப்போடுகிறதற்கு வனாந்தரத்திலே ஆணையிட்டேன்.' where book_id=25 and chapter_id=20 and verse_id= 24;
update tbl_bible set verse='அவர்கள் என் கட்டளைகளின்படி நடந்து, என் நியாயங்களைக் கைக்கொண்டு, அவைகளின்படி செய்ய நான் அவர்களுக்கு ஏக இருதயத்தை தந்து, அவர்கள் உள்ளத்தில் புதிய ஆவியைக்கொடுத்து, கல்லான இருதயத்தை அவர்கள் மாம்சத்திலிருந்து எடுத்துப்போட்டு, சதையான இருதயத்தை அவர்களுக்கு அருளுவேன்.' where book_id=25 and chapter_id=11 and verse_id= 19;
update tbl_bible set verse='அதுமுதல் பிலாத்து அவரை விடுதலைபண்ண வகைதேடினான். யூதர்கள் அவனை நோக்கி: இவனை விடுதலைபண்ணினால் நீர் இராயனுக்குச் சிநேகிதனல்ல; தன்னை ராஜாவென்கிறவனெவனோ அவன் ராயனுக்கு விரோதி என்று சத்தமிட்டார்கள்.' where book_id=42 and chapter_id=19 and verse_id= 12;
update tbl_bible set verse='நாங்கள் மனித பிரேதத்தினால் தீட்டுப்பட்டவர்கள்; குறித்த காலத்தில் இஸ்ரவேல் புத்திரரோடேகூடக் கர்த்தருக்குக் காணிக்கையைச் செலுத்தாதபடிக்கு, நாங்கள் விலக்கப்பட்டிருக்கவேண்டியது என்ன என்றார்கள்.' where book_id=3 and chapter_id=9 and verse_id= 7;
update tbl_bible set verse='சாமுவேல் சவுலைப் பார்த்து: புத்தியீனமாய்ச் செய்தீர்; உம்முடைய தேவனாகிய கர்த்தர் உமக்கு விதித்த கட்டளையைக் கைக்கொள்ளாமற்போனீர்; மற்றப்படி கர்த்தர் இஸ்ரவேலின் மேல் உம்முடைய ராஜ்யபாரத்தை என்றைக்கும் ஸ்திரப்படுத்துவார்.' where book_id=8 and chapter_id=13 and verse_id= 13;
