update tbl_bible set verse='நான் தேவனுடைய ராஜ்யத்தில் நவமான ரசத்தைப் பானம்பண்ணும் நாள்வரைக்கும் திராட்சப்பழரசத்தை இனி நான் பானம்பண்ணுவதில்லையென்று மெய்யாகவே உங்களுக்குச் சொல்லுகிறேன் என்றார்.' where book_id=40 and chapter_id=14 and verse_id= 25;
update tbl_bible set verse='நீ புதுவீட்டைக் கட்டினால், ஒருவன் உன் மெத்தையிலிருந்து விழுகிறதினாலே, நீ இரத்தப்பழியை உன் வீட்டின்மேல் சுமத்திக்கொள்ளாதபடிக்கு, அதற்குக் கைப்பிடிசுவரைக் கட்டவேண்டும்.' where book_id=4 and chapter_id=22 and verse_id= 8;
update tbl_bible set verse='அவன் அலங்காரம் அவன் தலையீற்றுக் காளையினுடைய அலங்காரத்தைப்போலவும், அவன் கொம்புகள் காண்டாமிருகத்தின் கொம்புகளைப்போலவும் இருக்கும்; அவைகளாலே ஜனங்களை ஏகமாய் தேசத்தின் கடையாந்தரங்கள்மட்டும் முட்டித் துரத்துவான்; அவைகள் எப்பிராயீமின் பதினாயிரங்களும் மனாசேயின் ஆயிரங்களுமானவைகள் என்றான்.' where book_id=4 and chapter_id=33 and verse_id= 17;
update tbl_bible set verse='ராமோத்தும் அதின் வெளிநிலங்களும், ஆனேமும் அதின் வெளிநிலங்களும்,' where book_id=12 and chapter_id=6 and verse_id= 73;
update tbl_bible set verse='அமத்சியா கர்த்தரை விட்டுப் பின்வாங்கின காலமுதற்கொண்டு எருசலேமிலிருந்தவர்கள் அவனுக்கு விரோதமாகக் கட்டுப்பாடுபண்ணிக்கொண்டார்கள்; அதினிமித்தம் அவன் லாகீசுக்கு ஓடிப்போனான்; ஆனாலும் அவன் பிறகே லாகீசுக்கு மனுஷரை அனுப்பினார்கள்; அவர்கள் அங்கே அவனைக் கொன்றுபோட்டு,' where book_id=13 and chapter_id=25 and verse_id= 27;
update tbl_bible set verse='ஜனங்களில் மற்றவர்களாகிய ஆசாரியரும், லேவியரும் வாசல் காவலாளரும், பாடகரும், நிதனீமியரும், தேசங்களின் ஜனங்களைவிட்டுப் பிரிந்து விலகி தேவனுடைய நியாயப்பிரமாணத்துக்குத் திரும்பின அனைவரும் அவர்கள் மனைவிகளும், அவர்கள் குமாரரும் அவர்கள் குமாரத்திகளுமாகிய அறிவும் புத்தியும் உள்ளவர்களெல்லாரும்,' where book_id=15 and chapter_id=10 and verse_id= 28;
update tbl_bible set verse='தேவனே, உமது நாமம் விளங்குகிறதுபோல உமது புகழ்ச்சியும் பூமியின் கடையாந்தரங்கள் பரியந்தமும் விளங்குகிறது; உமது வலதுகரம் நீதியால் நிறைந்திருக்கிறது.' where book_id=18 and chapter_id=48 and verse_id= 10;
update tbl_bible set verse='இதோ, திராணியும் வல்லமையுமுடைய ஒருவன் ஆண்டவரிடத்தில் இருக்கிறான்; அவன் கல்மழைப்போலவும், சங்காரப் புசல்போலவும், புரண்டுவருகிற பெருவெள்ளம்போலவும் வந்து, கையினாலே அதைத் தரையில் தள்ளிவிடுவான்.' where book_id=22 and chapter_id=28 and verse_id= 2;
update tbl_bible set verse='உங்கள் இருதயத்திலே கசப்பான வைராக்கியத்தையும் விரோதத்தையும் வைத்தீர்களானால், நீங்கள் பெருமைபாராட்டாதிருங்கள்; சத்தியத்திற்கு விரோதமாய்ப் பொய்சொல்லாமலுமிருங்கள்.' where book_id=58 and chapter_id=3 and verse_id= 14;
update tbl_bible set verse='பின்னும் தேவன் ஆபிரகாமை நோக்கி: இப்பொழுது நீயும், உனக்குப் பின் தலைமுறை தலைமுறையாக வரும் உன் சந்ததியும், என் உடன்படிக்கையைக் கைக்கொள்ளுங்கள்.' where book_id=0 and chapter_id=17 and verse_id= 9;
