update tbl_bible set verse='என் கண்களிலிருந்து இரவும்பகலும் ஓயாமல் கண்ணீர் ஓடிக்கொண்டிருக்கும்; என் ஜனமென்கிற குமாரத்தியாகிய கன்னிகை மகா வேதனையுள்ள அடியினாலும் கொடிய காயத்தினாலும் சேதப்பட்டிருக்கிறாள்.' where book_id=23 and chapter_id=14 and verse_id= 17;
