update tbl_bible set verse='ஆகையால், இதுமுதற்கொண்டு, நாங்கள் ஒருவனையும் மாம்சத்தின்படி அறியோம்; நாங்கள் கிறிஸ்துவையும் மாம்சத்தின்படி அறிந்திருந்தாலும், இனி ஒருபோதும் அவரை மாம்சத்தின்படி அறியோம்.' where book_id=46 and chapter_id=5 and verse_id= 16;
update tbl_bible set verse='தேசத்தின் ஜனங்களெல்லாரும் புதைத்துக்கொண்டிருப்பார்கள்; நான் மகிமைப்படும் அந்நாளிலே அது அவர்களுக்குக் கீர்த்தியாக இருக்கும் என்று கர்த்தராகிய ஆண்டவர் சொல்லுகிறார்.' where book_id=25 and chapter_id=39 and verse_id= 13;