update tbl_bible set verse='சிற்றின்பப்பிரியன் தரித்திரனாவான்; மதுபானத்தையும் எண்ணெயையும் விரும்புகிறவன் ஐசுவரியவானாவதில்லை.' where book_id=19 and chapter_id=21 and verse_id= 17;
update tbl_bible set verse='இஸ்ரவேலே, நீ திரும்புகிறதற்கு மனதாயிருந்தால் என்னிடத்தில் திரும்பு என்று கர்த்தர் சொல்லுகிறார்; நீ உன் அருவருப்புகளை என் பார்வையினின்று அகற்றிவிட்டால், நீ இனி அலைந்து திரிவதில்லை.' where book_id=23 and chapter_id=4 and verse_id= 1;
update tbl_bible set verse='இதோ, ஜாதிஜாதிக்குத் தீமைபரம்பும், பூமியின் எல்லைகளிலிருந்து மகா புசல் எழும்பும்.' where book_id=23 and chapter_id=25 and verse_id= 32;
