update tbl_bible set verse='கேளுங்கள், அப்பொழுது உங்களுக்குக் கொடுக்கப்படும்; தேடுங்கள், அப்பொழுது கண்டடைவீர்கள்; தட்டுங்கள் அப்பொழுது உங்களுக்குத் திறக்கப்படும்.' where book_id=39 and chapter_id=7 and verse_id= 7;
