update tbl_bible set verse='அவர்களின் பிறகே போய், அவர்களை நோக்கிக்கொண்டிருக்கிறதினால் இஸ்ரவேல் வம்சத்தார் எனக்குத் தங்கள் அக்கிரமத்தை நினைப்பூட்டாதபடிக்கு, இனி அவர்கள் இவர்களுடைய நம்பிக்கையாயிராமற்போவார்கள்; அப்பொழுது நான் கர்த்தராகிய ஆண்டவர் என்று அறிந்துகொள்வார்கள் என்கிறார் என்று சொல் என்றார்.' where book_id=25 and chapter_id=29 and verse_id= 16;
update tbl_bible set verse='மேலும், ஈசாயின் வேரும் புறஜாதியாரை ஆளும்படிக்கு எழும்புகிறவருமாகிய ஒருவர் தோன்றுவார்; அவரிடத்தில் புறஜாதியார் நம்பிக்கை வைப்பார்கள் என்று ஏசாயா சொல்லுகிறான்.' where book_id=44 and chapter_id=15 and verse_id= 12;
