update tbl_bible set verse='யாக்கோபு பதான் அராமிலிருந்து வந்தபின்பு தேவன் அவனுக்கு மறுபடியும் தரிசனமாகி, அவனை ஆசீர்வதித்து:' where book_id=0 and chapter_id=35 and verse_id= 9;
update tbl_bible set verse='நீ அவளை முற்றிலும் பகைத்துவிட்டாய் என்று நான் எண்ணி, அவளை உன் சிநேகிதனுக்குக் கொடுத்துவிட்டேன்; அவள் தங்கை அவளைப்பார்க்கிலும் அழகானவள் அல்லவா, அவளுக்குப் பதிலாக இவள் உனக்கு இருக்கட்டும் என்று சொன்னான்.' where book_id=6 and chapter_id=15 and verse_id= 2;
update tbl_bible set verse='அப்பொழுது சவுல்: தாவீதைக் கொண்டுவரச் சேவகரை அனுப்பினான்; அவர்கள் தீர்க்கதரிசனம் சொல்லுகிற தீர்க்கதரிசிகளின் கூட்டத்தையும், சாமுவேல் அவர்களின் தலைவனாக நிற்கிறதையும் கண்டார்கள்; அப்பொழுது சவுலினுடைய சேவகரின்மேல் தேவனுடைய ஆவி இறங்கினதினால் அவர்களும் தீர்க்கதரிசனம் சொன்னார்கள்.' where book_id=8 and chapter_id=19 and verse_id= 20;
update tbl_bible set verse='இவர்கள் யூதாவிலே உபதேசித்து, கர்த்தருடைய வேத புஸ்தகத்தை வைத்துக்கொண்டு, யூதாவின் பட்டணங்களிலெல்லாம் திரிந்து ஜனங்களுக்குப்போதித்தார்கள்.' where book_id=13 and chapter_id=17 and verse_id= 9;
