update tbl_bible set verse='இரண்டு மூன்று பட்டணங்களின் மனுஷர் தண்ணீர் குடிக்க பட்டணத்துக்குப் போய் அலைந்தும் தாகந்தீர்த்துக்கொள்ளவில்லை; ஆகிலும் நீங்கள் என்னிடத்தில் திரும்பாமற்போனீர்கள் என்று கர்த்தர் சொல்லுகிறார்.' where book_id=29 and chapter_id=4 and verse_id= 8;
update tbl_bible set verse='மறுநாளிலே ஜனங்களுடைய அதிகாரிகளும் மூப்பரும் வேதபாரகரும்,' where book_id=43 and chapter_id=4 and verse_id= 5;
update tbl_bible set verse='நாங்கள் இந்தத் திராட்சத் துருத்திகளை நிரப்புகையில் புதிதாயிருந்தது; ஆனாலும், இதோ, கிழிந்துபோயிற்று; எங்கள் வஸ்திரங்களும் பாதரட்சைகளும் நெடுந்தூரமான பிரயாணத்தினாலே பழசாய்ப்போயிற்று என்றார்கள்.' where book_id=5 and chapter_id=9 and verse_id= 13;
update tbl_bible set verse='இப்படியே அவர்களுடைய இரத்தப்பழி என்றும் யோவாபுடைய தலையின் மேலும், அவன் சந்ததியாரின் தலையின் மேலும் திரும்பவும், தாவீதுக்கும் அவர் சந்ததியாருக்கும் அவர் வீட்டாருக்கும் அவர் சிங்காசனத்திற்கும் என்றென்றைக்கும் கர்த்தராலே சமாதானம் உண்டாயிருக்கவும் கடவது என்றான்.' where book_id=10 and chapter_id=2 and verse_id= 33;
update tbl_bible set verse='ஆகையால், இதோ, நாட்கள்வரும், அப்பொழுது இந்த ஸ்தலம் தோப்பேத்தென்றும், இன்னோமுடைய குமாரனின் பள்ளத்தாக்கென்றும் இனிச் சொல்லப்படாமல், சங்காரப்பள்ளத்தாக்கென்று சொல்லப்படும்.' where book_id=23 and chapter_id=19 and verse_id= 6;
