update tbl_bible set verse='இந்த ஜனங்கள் கர்த்தருடைய ஆலயத்தைக் கட்டுகிறதற்கு ஏற்றகாலம் இன்னும் வரவில்லை என்கிறார்கள் என்று சேனைகளின் கர்த்தர் சொல்லுகிறார்.' where book_id=36 and chapter_id=1 and verse_id= 2;
update tbl_bible set verse='அவர்களெல்லாரும் தங்கள் பரிபூரணத்திலிருந்தெடுத்துத் தேவனுக்கென்று காணிக்கை போட்டார்கள்; இவளோ தன்வறுமையிலிருந்து தன் ஜீவனத்துக்கு உண்டாயிருந்ததெல்லாம் போட்டு விட்டாள் என்றார்.' where book_id=41 and chapter_id=21 and verse_id= 4;
update tbl_bible set verse='உம்மை இஸ்ரவேலின் சிங்காசனத்தின்மேல் வைக்க, உம்மேல் பிரியங்கொண்ட உம்முடைய தேவனாகிய கர்த்தர் ஸ்தோத்திரிக்கப்படுவாராக: கர்த்தர் இஸ்ரவேலை என்றைக்கும் சிநேகிக்கிறபடியினால், நியாயமும் நீதியும் செய்கிறதற்கு உம்மை ராஜாவாக ஏற்படுத்தினார் என்றாள்.' where book_id=10 and chapter_id=10 and verse_id= 9;
