update tbl_bible set verse='அவன்: ஐயோ! நசரேயனாகிய இயேசுவே, எங்களுக்கும் உமக்கும் என்ன? எங்களைக்கெடுக்கவா வந்தீர்? உம்மை இன்னாரென்று அறிவேன்; நீர் தேவனுடைய பரிசுத்தர் என்று உரத்தசத்தமிட்டான்.' where book_id=41 and chapter_id=4 and verse_id= 34;
update tbl_bible set verse='இயேசு மார்த்தாளிடத்திலும் அவளுடைய சகோதரியினிடத்திலும் லாசருவினிடத்திலும் அன்பாயிருந்தார்.' where book_id=42 and chapter_id=11 and verse_id= 5;