update tbl_bible set verse='அப்பொழுது கேருபீன்களுக்குள்ளே ஒரு கேருபீன் தன் கையைக் கேருபீன்களின் நடுவில் இருக்கிற அக்கினியில் நீட்டி, அதில் எடுத்து, சணல்நூல் அங்கி தரித்திருந்த புருஷனுடைய கையில் கொடுத்தான்; அவன் அதை வாங்கிக் கொண்டு வெளியே வந்தான்.' where book_id=25 and chapter_id=10 and verse_id= 7;
update tbl_bible set verse='யுத்தங்களையும் யுத்தங்களின் செய்திகளையும் கேள்விப்படும்போது கலங்காதேயுங்கள்; இவைகள் சம்பவிக்கவேண்டியதே; ஆனாலும் முடிவு உடனே வராது.' where book_id=40 and chapter_id=13 and verse_id= 7;
update tbl_bible set verse='முப்பத்தெட்டு வருஷம் வியாதிகொண்டிருந்த ஒரு மனுஷன் அங்கே இருந்தான்.' where book_id=42 and chapter_id=5 and verse_id= 5;
update tbl_bible set verse='வாயானது போஜனத்தை ருசிபார்க்கிறதுபோல, செவியானது வார்த்தைகளைச் சோதித்துப்பார்க்கும்.' where book_id=17 and chapter_id=34 and verse_id= 3;